import { useState ,useEffect} from "react"
import Selector from "./Selector"
import { useDispatch, useSelector } from "react-redux"


export default function Home(){
    //const dispatch=useDispatch()
    
    const intervalo=useSelector(state=>state.intervalo)

    const[local,setlocal]=useState({
        
        hora:"",
        zona:"",
      
    })
    
    useEffect(()=>{calculo()},[])
   

    function calculo(){
    let date=new Date()
     setlocal({...local,["hora"]:date.getHours(),["zona"]:date.getTimezoneOffset()/60})
    
    }
   

   
   

    return(
        <div>
            <div>
                hora:{local.hora}
                zona:{local.zona}
                ref:{local.hora+local.zona}
            </div>
            {intervalo.length>0&&intervalo.map((e,i)=>{return ( <div key={i+2}>
                                            <Selector key={i} index={i} intervalo={intervalo}/> 
                                        </div>
                )
              
                })}

            <div>
                <button>enviar horarios</button>
            </div>
        
           {console.log("home")}
           
        </div>

    )
}
