import { useEffect } from "react"
import { useState } from "react"
import Selector from "./Selector"
import { useDispatch, useSelector } from "react-redux"
import { deletedintervalo } from "../redux/actions"


export default function Home(){
    const dispatch=useDispatch()
    let mostra=[]
    const intervalo=useSelector(state=>state.intervalo)

    const[local,setlocal]=useState({
        
        hora:"",
        zona:"",
      
    })
    
    useEffect(()=>{calculo()},[])
   

    function calculo(){
    let date=new Date()
     setlocal({...local,["hora"]:date.getHours(),["zona"]:date.getTimezoneOffset()/60})
    
    }
    if(intervalo.length>0){mostra=intervalo}

   


    return(
        <div>
            <div>
                hora:{local.hora}
                zona:{local.zona}
                ref:{local.hora+local.zona}
            </div>
            {intervalo.length>0&&mostra.map((e,i)=>{return ( <div key={i+2}>
                                            <Selector key={i} index={i}/> 
                                             <button key={i+1}onClick={()=>{dispatch(deletedintervalo(i,mostra))}}>eliminar</button>
                                        </div>
                )
              
                })}
        
        
           {console.log(mostra)}
           {console.log(intervalo)}
        </div>

    )
}
