import { useEffect } from "react"
import { useState } from "react"
import Selector from "./Selector"


export default function Home(){
    const[local,setlocal]=useState({
        
        hora:"",
        zona:"",
      
    })
    const[cantidad,setcantidad]=useState([1])
    useEffect(()=>{calculo()},[])

    function calculo(){
    let date=new Date()
     setlocal({...local,["hora"]:date.getHours(),["zona"]:date.getTimezoneOffset()/60})
    // hora=date.getHours()
    // zona=date.getTimezoneOffset()/60
    }


    return(
        <div>
            <div>
                hora:{local.hora}
                zona:{local.zona}
                ref:{local.hora+local.zona}
            </div>
            {cantidad.length>0&&cantidad.map((e,i)=>{return <Selector key={i} setcantidad={setcantidad}/>})}
        
        
           
        </div>

    )
}
