import { useEffect } from "react"
import { useState } from "react"


export default function Home(){
    const[local,setlocal]=useState({
        
        hora:"",
        zona:""
    })
    useEffect(()=>{calculo()})

    function calculo(){
    let date=new Date()
     setlocal({["hora"]:date.getHours(),["zona"]:date.getTimezoneOffset()/60})
    // hora=date.getHours()
    // zona=date.getTimezoneOffset()/60
    }


    return(
        <div>
            <div>
                hora:{local.hora}
                zona:{local.zona}
                ref:{local.hora+local.zona}
            </div>
        </div>

    )
}
