import { DELETEINTERVALO, INTERVALO } from "./actions"



const initialSate={
    intervalo:[1],
    zona:"",
}



export default function rootReducer(state=initialSate,actions){
    switch(actions.type){
        case INTERVALO:
            let i=0
            let nuevoIntervalo=state.intervalo
            console.log(nuevoIntervalo)
            let modificar=state.intervalo.reduce((e,i)=>{return e.index===actions.payload.index})
            console.log(modificar)
            
            if(modificar&&state.intervalo.length>1){nuevoIntervalo[i]=actions.payload; return{...state,intervalo:nuevoIntervalo}}
            else{
            return{
                ...state,
                intervalo:[...state.intervalo,actions.payload]}
            }
        case DELETEINTERVALO:
            return{
                ...state,
                intervalo:actions.payload.length>0?actions.payload:[1]
            }    
        default:
            return state
    }
}
