import { DELETEINTERVALO, INTERVALO } from "./actions"



const initialSate={
    intervalo:[1],
    zona:"",
}



export default function rootReducer(state=initialSate,actions){
    switch(actions.type){
        case INTERVALO:
            let nuevoIntervalo=state.intervalo
            let modificar=state.intervalo.map((e,i)=>{return e.index===actions.payload.index})
            
            
            if(modificar){ return{...state,intervalo:nuevoIntervalo}}
            return{
                ...state,
                intervalo:[...state.intervalo,actions.payload]
            }
        case DELETEINTERVALO:
            return{
                ...state,
                intervalo:actions.payload.length>0?actions.payload:[1]
            }    
        default:
            return state
    }
}
