import { useEffect } from "react"
import { useState } from "react"


export default function Home(){
    const[local,setlocal]=useState({
        date:"",
        hora:"",
        zona:""
    })
    useEffect(()=>{calculo()},[])

    function calculo(){
     date=new Date()
     hora=date.getHours()
     zona=date.getTimezoneOffset()/60
    }


    return(
        <div>
            <div>
                hora:{local.hora}
                zona:{zona}
                ref:{hora+zona}
            </div>
        </div>

    )
}
